import { useState } from 'react';

function App() {
  const [todo, setTodo] = useState('');
  const [todoList, setTodoList] = useState([]);
  const onChangeHandler = (e) => setTodo(e.target.value);
  const onSubmit = (e) => {
    e.preventDefault();
    if (todo !== '') {
      setTodoList((currentArray) => [todo, ...currentArray]); //State는 언제나 New 해야 한다.
      setTodo('');

      setTodoList('');
      console.log(todo);
    }
  };

  return (
    <div>
      <form onSubmit={onSubmit}>
        <input value={todo} onChange={onChangeHandler} />
        <button>작성</button>
      </form>
    </div>
  );
}

export default App;
